<body><div style="width:600px; margin:0 auto; font-family:arial,helvetica,sans-serif; font-size:12px;">   <h1>All active sites by brand <span style="font-weight:normal; font-size:12px;">&nbsp; &nbsp; <?=date('d M Y');?></span></h1>   <?php $now_brand = ''; ?>   <?php $now_site = ''; ?>   <?php foreach($sites as $site): ?>      <?php if ($site['BrandName'] != $now_brand): ?>         <?php $now_brand = $site['BrandName']; ?>   <div style="background-color:#000; color:#FFF; margin-top:12px; padding:6px 0 5px 9px; font-weight:bold;"><?=$site['BrandName'];?></div>      <?php endif; ?>      <?php if ($site['SiteID'] != $now_site): ?>         <?php $now_site = $site['SiteID']; ?>   <div style="border-top:2px solid #000; margin-top:6px; padding-top:4px; font-weight:bold;"><span style="padding-left:9px; font-size:14px;"><?=$site['Domain'];?></span></div>      <?php endif; ?>      <div style="margin-left:2em; padding-top:3px;">   <?=$site['Service'];?> &mdash; <span style="font-size:11px;"><?=$site['VendorName'];?></span>   </div>   <?php endforeach; ?>   </div></body>