<body><?=$this->load->view('cp/tabs');?><div id="Wrapper">     <div class="container">   <table class="layout">   <tr>   <td class="left">      <div class="Left">         <div class="col">            <div class="page-header">               <div class="page-header-links">                   <a class="admin" href="<?=site_url('cp/vendors/edit/'.$vendor_id.'/'.$last_action);?>">Edit</a> | <a class="admin" href="<?=site_url('cp/vendors/index/');?>">Done</a>               </div>   <h1>About this vendor</h1>            </div>            <div class="innercol">      <div id="vendor-view">      <div class="vendor-head">   <h1><?=$vendor['VendorName'];?></h1>   <p><?=str_replace("\n", "<br />", $vendor['Address']);?>   <br /><a href="<?=$vendor['VendorURL'];?>" target="_blank"><?=$vendor['VendorURL'];?></a></p>   </div>   <h2>About <?=$vendor['VendorName'];?></h2>      <?=$vendor['AboutThisVendor'];?>      <h2>Current Clients</h2>      <?php if ( ! empty($current_clients)): ?>      <ul>      <?php foreach ($current_clients AS $client): ?>      <li><a class="domain-head" href="<?=site_url('sites/vendors/index/'.$client['SiteID']);?>"><?=$client['Domain'];?></a>      <br /><strong><?=$client['ServiceName'];?></strong>: <?=word_limiter(strip_tags($client['ServiceDesc']), 9);?></li>      <?php endforeach; ?>      </ul>   <?php else: ?>         <p>This vendor has no current clients.</p>      <?php endif; ?>      <h2>Former Clients</h2>      <?php if ( ! empty($former_clients)): ?>      <ul>      <?php foreach ($former_clients AS $client): ?>      <li><a class="domain-head" href="<?=site_url('sites/vendors/index/'.$client['SiteID']);?>"><?=$client['Domain'];?></a>      <br /><strong><?=$client['ServiceName'];?></strong>: <?=word_limiter(strip_tags($client['ServiceDesc']), 9);?></li>      <?php endforeach; ?>      </ul>   <?php else: ?>         <p>This vendor has no former clients.</p>      <?php endif; ?>      <h2>Domains</h2>      <?php if ( ! empty($domains)): ?>      <ul>      <?php foreach ($domains AS $domain): ?>      <li><a class="domain-head" href="/admin/domains.php/domains/edit/<?=$domain['ID'];?>/<?=$last_action;?>"><?=$domain['Domain'];?></a>      &mdash;          <?php if ($domain['RegistrarVendor'] == $vendor_id && $domain['DNSVendor'] == $vendor_id): ?>Registrar &amp; DNS Provider         <?php elseif ($domain['RegistrarVendor'] == $vendor_id): ?>Registrar         <?php elseif ($domain['DNSVendor'] == $vendor_id): ?>DNS Provider         <?php endif; ?>         </li>      <?php endforeach; ?>      </ul>   <?php else: ?>         <p>This vendor does not manage any domains.</p>      <?php endif; ?>      </div>  <?php /* vender-view */ ?>               </div>   <!-- innercol -->         </div>   <!-- col -->         <div class="bottom">&nbsp;</div>         <div id="Footer">   &copy; 2007-<?=date('Y');?> The Hain Celestial Group, Inc.        </div>   <!-- Footer -->      </div>   <!-- Left -->   </td>   <td class="right">      <div class="Right">         <div class="col">                        <a href="<?=site_url('cp/site_vendors/add/'.$vendor_id.'/'.$last_action);?>" style="background-color:transparent;"><img src="/images/buttons/button_new_client.gif" width="138" height="31" alt="Add a new client" style="border:0px; margin-top:4px;" /></a>                     </div>   <!-- col -->      </div>   <!-- Right -->   </td>   </tr>   </table>   </div>   <!-- class="container" --></div>   <!-- Wrapper --></body>