<body><div style="width:600px; margin:0 auto; font-family:arial,helvetica,sans-serif; font-size:12px;">   <h1>All domains by brand <span style="font-weight:normal; font-size:12px;">&nbsp; &nbsp; <?=date('d M Y');?></span></h1>      <p>Total Domains: <?=$total_domains;?></p>   <?php $now_brand = ''; ?>   <?php foreach($domains as $site_id => $site): ?>         <?php if ($site['brand'] != $now_brand): ?>         <?php $now_brand = $site['brand']; ?>   <div style="background-color:#000; color:#FFF; margin-top:12px; padding:6px 0 5px 9px; font-weight:bold;"><?=$site['brand'];?></div>      <?php endif; ?>   <div style="border-top:2px solid #000; margin-top:6px; padding:6px 0 6px 0; font-weight:bold;"><span style="padding-left:9px; font-size:14px;"><?=$site['primary'];?></span></div>      <?php $site_domains = $site['domains']; ?>      <?php for ($j=0; $j<count($site_domains); $j++): ?>            <?php         $registrar_notes = array();         $dns_notes = array();         // Registrar should be at primary...         if ($site_domains[$j]['RegistrarShouldBePrimary'] == 1)         {            // ...but it is NOT primary...            if ($site_domains[$j]['RegistrarName'] != $primary_registrar['VendorName'])            {               // ...and is KNOWN...               if ($site_domains[$j]['RegistrarName'] != 'Unknown Vendor')               {                  $registrar_notes[] = 'transfer registrar to primary';               }               // ...and is UNKNOWN...               else               {                  $registrar_notes[] = 'transfer <b>unknown</b> registrar to primary';               }            }         }         // Registrar should NOT be at primary...         else         {            // ...and is KNOWN...            if ($site_domains[$j]['RegistrarName'] != 'Unknown Vendor')            {               $registrar_notes[] = 'remote registrar';            }            // ...and is UNKNOWN...            else            {               $registrar_notes[] = 'unknown registrar';            }         }         // DNS should be at primary...         if ($site_domains[$j]['DNSShouldBePrimary'] == 1)         {            // ...it is at primary...            if ($site_domains[$j]['DNSName'] == $primary_dns_vendor['VendorName'])            {               // ...but is not set up               if ($site_domains[$j]['PrimaryDNSIsSetUp'] == 0)               {                  $dns_notes[] = 'add to primary DNS';               }            }            // ...it is NOT at primary...            elseif ($site_domains[$j]['DNSName'] != $primary_dns_vendor['VendorName'])            {               // ...it is KNOWN...               if ($site_domains[$j]['DNSName'] != 'Unknown Vendor')               {                  // ...and is not set up                  if ($site_domains[$j]['PrimaryDNSIsSetUp'] == 0)                  {                     $dns_notes[] = 'add to primary DNS';                     $dns_notes[] = 'change DNS vendor to primary';                  }                  // ...and it is set up                  else                  {                     $dns_notes[] = 'change DNS vendor to primary';                  }               }               // ...it is UNKNOWN...               else               {                  // ...and is not set up                  if ($site_domains[$j]['PrimaryDNSIsSetUp'] == 0)                  {                     $dns_notes[] = 'add to primary DNS';                     $dns_notes[] = 'change <b>unknown</b> DNS vendor to primary';                  }                  // ...and it is set up                  else                  {                     $dns_notes[] = 'change <b>unknown</b> DNS vendor to primary';                  }               }            }         }         // DNS should NOT be at primary...         else         {            // ...but it is UNKNOWN...            if ($site_domains[$j]['DNSName'] == 'Unknown Vendor')            {               $dns_notes[] = 'unknown DNS vendor';            }            else            {               $dns_notes[] = 'remote DNS vendor';            }         }         ?>      <div style="margin-left:2em; padding:3px 0; border-top:1px solid #DCDCDC;">         <div class="domain">         <strong><?php if ($site_domains[$j]['PrimaryDomain'] == 1): ?><i><?php endif; ?><?=$site_domains[$j]['Domain'];?><?php if ($site_domains[$j]['PrimaryDomain'] == 1): ?> (primary)</i><?php endif; ?></strong>            <div style="margin-left:1em; padding:3px 0;">Registrar &mdash; <span style="font-size:11px;"><?=$site_domains[$j]['RegistrarName'];?></span></div>            <?php if ( ! empty($registrar_notes)): ?><?php $first = true; ?><div style="margin:3px 0; margin-left:2em; border-left:6px solid #000; padding-left:6px;">Notes<?php foreach ($registrar_notes AS $note): ?><?php if ($first): ?> &mdash; <?php $first = false; ?><?php else: ?> | <?php endif; ?><?=$note;?><?php endforeach; ?></div><?php endif; ?>            <div style="margin-left:1em; padding:3px 0;">DNS Vendor &mdash; <span style="font-size:11px;"><?=$site_domains[$j]['DNSName'];?></span></div>            <?php if ( ! empty($dns_notes)): ?><?php $first = true; ?><div style="margin:3px 0; margin-left:2em; border-left:6px solid #000; padding-left:6px;">Notes<?php foreach ($dns_notes AS $note): ?><?php if ($first): ?> &mdash; <?php $first = false; ?><?php else: ?> | <?php endif; ?><?=$note;?><?php endforeach; ?></div><?php endif; ?>         </div>      </div>      <?php endfor; ?>         <?php endforeach; ?>   </div></body>