ist AS $faq): ?>               <?php $num_answers = count($answers[$faq['ID']]); ?>         <?php if ($faq['CategoryID'] == $category['ID']): ?>      <div style="border-top:1px solid #666; clear:both;">      <p style="margin:0; padding:4px 0;"><?php if ($faq['Status'] != 'active'): ?>(<?=$faq['Status'];?>) <?php endif; ?><a href="<?=site_url('cp/faqs/edit/'.$site_id.'/'.$faq['ID'].'/'.$last_action);?>"style="text-decoration:none;"><?=nl2br($faq['ShortQuestion']);?></a><?php if ($faq['FlagAsNew'] == 1): ?>&nbsp;&nbsp;<span style="color:red;">NEW!</span><?php endif; ?>&nbsp;&nbsp;(<?=$num_answers;?> <?=($num_answers == 1) ? 'answer' : 'answers';?>)</p>      </div>               <?php endif; ?>         <?php endforeach; ?>   </div> <?php // listing ?>   <?php endforeach; ?><?php else: ?>   <p>There are no FAQs to display.</p>      <p><a class="admin" href="<?=site_url('cp/faqs/add/'.$site_id.'/'.$last_action);?>">Create the first FAQ.</a></p><?php endif; ?>            </div>   <!-- innercol -->         </div>   <!-- col -->         <div class="bottom">&nbsp;</div>         <div id="Footer">   &copy; 2009 The Hain Celestial Group, Inc.        </div>   <!-- Footer -->      </div>   <!-- Left -->   </td>   <td class="right">      <div class="Right">         <div class="col">                  <a href="<?=site_url('cp/faqs/add/'.$site_id.'/'.$last_action);?>" style="background-color:transparent;"><img src="/images/buttons/button_new_faq.gif" width="138" height="31" alt="Add a new FAQ" style="border:0px; margin:4px 0 24px 0;" /></a>            <h1>Jump to...</h1>   <?php if ( ! empty($nocat_list)): ?>      <div style="margin-left:12px;">      <p style="margin:0; padding:4px 0;"><a href="#nocat" style="text-decoration:none;">No Category</a></p>      </div>   <?php endif; ?>   <?php foreach($category_list AS $cat): ?>      <?php if ($cat['level'] > 0): ?>      <div style="margin-left:<?=($cat['level']-1)*12+12;?>px;"> 