<div class="page-header">   <div class="page-header-links">&nbsp;</div>   <h2>On-Hold Job Listings</h2></div><div id="listing"><?php if ($jobs['job_exists'] == true): ?>   <table width="100%" cellpadding="0" cellspacing="0" border="0">   <tr>   <th>Job No.</th>   <th>Job Title</th>   <th>&nbsp;</th>   <th>Put on Hold</th>   <th>Manager</th>   <tr>      <?php foreach($job_list as $job): ?>   <tr>   <td valign="top"><?=$job['JobNum'];?></td>   <td valign="top"><strong><?=$job['Title'];?></strong></td>   <td valign="top">Loc: <span class="dark"><?=$job['Location'];?></span>   <br>Cat: <span class="dark"><?=$job['Category'];?></span>   <br>Co:&nbsp; <span class="dark"><?=$job['Company'];?></span>   </td>   <td valign="top"><?=date('d M Y', strtotime($job['OnHoldDate']));?>   <br>by <?=$job['OnHoldByName'];?>   <br>Notes: <span class="dark"><?=character_limiter($job['OnHoldNotes'], 30);?></span></td>   <td valign="top"><?=$job['Manager'];?></td>   <tr>   <?php endforeach; ?>   <tr>   <td class="hidden" width="80"><img src="/images/dot_clear.gif" width="80" height="1" alt=""></td>   <td class="hidden" width="240"><img src="/images/dot_clear.gif" width="240" height="1" alt=""></td>   <td class="hidden" width="240"><img src="/images/dot_clear.gif" width="240" height="1" alt=""></td>   <td class="hidden" width="240"><img src="/images/dot_clear.gif" width="240" height="1" alt=""></td>   <td class="hidden" width="120"><img src="/images/dot_clear.gif" width="120" height="1" alt=""></td>   </tr>   </table></div><p>Printed <?=date('d M Y');?><?php else: ?>   <p>There are no on-hold jobs to display.</p><?php endif; ?>