<?php// go though once to set the product thumbnail imagesforeach($product_list AS $product){   if ( ! file_exists(DOCPATH.'/images/products/thumb/'.$product['ThumbFile']))   {      $product['ThumbFile'] = 'placeholder.jpg';   }}?><div id="product-list"><?php foreach($category_list AS $category): ?>   <?php   $my_products = array();   foreach($product_list AS $product)   {      if ($product['CategoryID'] == $category['CategoryID'])      {         $my_products[] = $product;      }   }   ?>      <?php if ( ! empty($my_products)): ?><div class="listing clearfix">      <h2><?=$category['CategoryName'];?></h2>         <?php foreach($my_products AS $my_product): ?>         <div class="product-item">      <div class="product-image"><img src="/images/products/w080/<?=$my_product['ThumbFile'];?>" width="<?=$my_product['ThumbWidth'];?>" height="<?=$my_product['ThumbHeight'];?>" alt="<?=$my_product['ThumbAlt'];?>" style="float:left;" /></div>      <div class="product-info">         <div class="product-name"><?=$my_product['ProductName'];?></div>         <div class="product-details">Available in <?=$my_product['AvailableIn'];?></div>      </div>   </div>            <?php endforeach; ?></div>  <?php // listing ?>   <?php endif; ?><?php endforeach; ?></div>  <?php // product-list ?>