<body id="workflow"><?=$this->load->view('cp/tabs');?><div id="Wrapper">  <?php if ($admin['message'] != ''): ?><div id="flash_alert"><?=$admin['message'];?></div><?php endif; ?><?php if ($admin['error_msg'] != ''): ?><div id="flash_error"><?=$admin['error_msg'];?></div><?php endif; ?>   <div class="container">   <table class="layout">   <tr>   <td class="left">      <div class="Left">         <div class="col">                            <div class="page-header">               <div class="page-header-links">   <a class="admin" href="<?=site_url('cp/workflows/wf_print/'.$project['ID']);?>" onclick="popup(this.href, 'report'); return false;">Print</a>               </div>   <!-- page_header_links -->   <h1><a href="<?=site_url('cp/dashboards/index/'.$project_id);?>">Dashboard</a> |    <strong>Workflow</strong> |    <a href="<?=site_url('cp/projects/edit/'.$project_id.'/'.$last_action);?>">Project Settings</a></h1>            </div>   <!-- page_header -->            <div class="innercol"><h1 style="margin-bottom:12px;"><span style="font-weight:bold;"><?=$project['Group'];?></span> <?=$project['ProjectName'];?></h1><?php foreach ($phases AS $phase): ?><div class="section">   <h2><span style="background:#<?=$phase['BgColor'];?>; color:#<?=$phase['Color'];?>; padding:2px 8px 3px 8px;"><?=$phase['ItemName'];?></span></h2>   <?php for ($j=0; $j<count($tasks); $j++): ?>      <?php if ($tasks[$j]['ParentID'] == $phase['ID'] && $tasks[$j]['Status'] != 2): ?>   <div class="task">      <div class="status">         <?php if ($tasks[$j]['Status'] == 0): ?>      <a class="admin" href="<?=site_url('cp/workflows/remove/'.$project['ID'].'/'.$tasks[$j]['ID']);?>">Remove</a> or       <form action="<?=site_url('cp/workflows/complete/'.$project['ID'].'/'.$tasks[$j]['ID']);?>" method="post">      <input type="image" src="/images/projects/btn_complete.gif" width="77" height="21" alt="Complete">      </form>         <?php else: ?>      <span>Completed</span> <?=date('d M Y', strtotime($tasks[$j]['CompletedDate']));?> by <?=$tasks[$j]['CompletedBy'];?> <a class="admin" href="<?=site_url('cp/workflows/uncomplete/'.$project['ID'].'/'.$tasks[$j]['ID']);?>">[x]</a>         <?php endif; ?>      </div>      <p><?=$tasks[$j]['ItemName'];?></p>         <?php if (is_array($tasks[$j]['Tools'])): ?>      <p class="tools">            <?php for ($k=0; $k<count($tasks[$j]['Tools']); $k++): ?>               <?php if ($k != 0): ?>      <span class="pipe">|</span>               <?php endif; ?>      <a class="admin" href="<?=$tasks[$j]['Tools'][$k]['URL'];?>"><?=$tasks[$j]['Tools'][$k]['ItemName'];?></a>            <?php endfor; ?>         <?php endif; ?>      </p>   </div>         <?php endif; ?>   <?php endfor; ?>   </div><?php endforeach; ?><?php if ($removed_exists == TRUE): ?><div id="removed"><h2>Removed Items</h2><?php foreach ($phases AS $phase): ?>   <?php if ($phase['has_removed'] == TRUE): ?>   <p style="color:#333; padding-bottom:0; margin-bottom:0; font-weight:bold; padding-left:12px;"><?=$phase['ItemName'];?></b>   <ul style="padding-top:0; margin-top:3px;">      <?php for ($j=0; $j<count($tasks); $j++): ?>         <?php if ($tasks[$j]['ParentID']  == $phase['ID'] && $tasks[$j]['Status'] == 2): ?>      <li><a class="admin" href="<?=site_url('cp/workflows/unremove/'.$project['ID'].'/'.$tasks[$j]['ID']);?>">Re-add</a> <?=$tasks[$j]['ItemName'];?></li>         <?php endif; ?>      <?php endfor; ?>   </ul>   <?php endif; ?><?php endforeach; ?></div><?php endif; ?>            </div>   <!-- innercol -->         </div>   <!-- col -->         <div class="bottom">&nbsp;</div>         <div id="Footer">   &copy; 2007 The Hain Celestial Group, Inc.        </div>   <!-- Footer -->      </div>   <!-- Left -->   </td>   <td class="right">      <div class="Right">         <div class="col">                     </div>   <!-- col -->      </div>   <!-- Right -->   </td>   </tr>   </table>         </div>   <!-- class="container" --></div>   <!-- Wrapper --></body>