<body id="workflow"><div id="dataarea"><h1 style="font-size:22px;"><?=$project['ProjectName'];?></h1><div class="page-header">   <div class="page-header-links">&nbsp;</div>   <h2>Workflow for this project</h2></div><?php if ($project['error_msg'] != ""): ?><div class="error">ERROR: <?=$project['error_msg'];?></div><?php endif; ?><?php foreach ($phases AS $phase): ?><div class="section">   <h2><span style="background:#<?=$phase['BgColor'];?>; color:#<?=$phase['Color'];?>; padding:2px 8px 3px 8px;"><?=$phase['ItemName'];?></span></h2>   <?php for ($j=0; $j<count($tasks); $j++): ?>      <?php if ($tasks[$j]['ParentID'] == $phase['ID'] && $tasks[$j]['Status'] != 2): ?>   <div class="task">      <div class="status">         <?php if ($tasks[$j]['Status'] == 0): ?>      <a class="admin" href="<?=site_url('cp/workflows/remove/'.$project['ID'].'/'.$tasks[$j]['ID']);?>">Remove</a> or       <form action="<?=site_url('cp/workflows/complete/'.$project['ID'].'/'.$tasks[$j]['ID']);?>" method="post">      <input type="image" src="/images/projects/btn_complete.gif" width="77" height="21" alt="Complete">      </form>         <?php else: ?>      <span>Completed</span> <?=date('d M Y', strtotime($tasks[$j]['CompletedDate']));?> by <?=$tasks[$j]['CompletedBy'];?> <a class="admin" href="<?=site_url('cp/workflows/uncomplete/'.$project['ID'].'/'.$tasks[$j]['ID']);?>">[x]</a>         <?php endif; ?>      </div>      <p><?=$tasks[$j]['ItemName'];?></p>         <?php if (is_array($tasks[$j]['Tools'])): ?>      <p class="tools">            <?php for ($k=0; $k<count($tasks[$j]['Tools']); $k++): ?>               <?php if ($k != 0): ?>      <span class="pipe">|</span>               <?php endif; ?>      <a class="admin" href="<?=$tasks[$j]['Tools'][$k]['URL'];?>"><?=$tasks[$j]['Tools'][$k]['ItemName'];?></a>            <?php endfor; ?>         <?php endif; ?>      </p>   </div>         <?php endif; ?>   <?php endfor; ?>   </div><?php endforeach; ?><?php if ($removed_exists == TRUE): ?><div id="removed"><h2>Removed Items</h2><?php foreach ($phases AS $phase): ?>   <?php if ($phase['has_removed'] == TRUE): ?>   <p style="color:#333; padding-bottom:0; margin-bottom:0; font-weight:bold; padding-left:12px;"><?=$phase['ItemName'];?></b>   <ul style="padding-top:0; margin-top:3px;">      <?php for ($j=0; $j<count($tasks); $j++): ?>         <?php if ($tasks[$j]['ParentID']  == $phase['ID'] && $tasks[$j]['Status'] == 2): ?>      <li><a class="admin" href="<?=site_url('cp/workflows/unremove/'.$project['ID'].'/'.$tasks[$j]['ID']);?>">Re-add</a> <?=$tasks[$j]['ItemName'];?></li>         <?php endif; ?>      <?php endfor; ?>   </ul>   <?php endif; ?><?php endforeach; ?></div><?php endif; ?></div> <?php // dataarea ?></body>